$(document).ready( function() {
    $('#myCarousel').carousel({
		interval:   4000
	});
	
	var clickEvent = false;
	$('#myCarousel').on('click', '.sliddd a', function() {
			clickEvent = true;
			$('.sliddd li').removeClass('active');
			$(this).parent().addClass('active');		
	}).on('slid.bs.carousel', function(e) {
		if(!clickEvent) {
			var count = $('.sliddd').children().length -1;
			var current = $('.sliddd li.active');
			current.removeClass('active').next().addClass('active');
			var id = parseInt(current.data('slide-to'));
			if(count == id) {
				$('.sliddd li').first().addClass('active');	
			}
		}
		clickEvent = false;
	});
	
	$(function(){
		$("#registor").ajaxForm({
			beforeSend:function(){
				$("#result").html("<img src='images/loading.gif' width='50px' style='margin: 20px 0;'/>");
			},success:function(data){
				$("#result").html(data);
			}
		});
	});
	
	$(function(){
		$("#login").ajaxForm({
			beforeSend:function(){
				$("#log_result").html("<img src='images/loading.gif' width='30px' style='margin: 20px 0;'/>");
			},success:function(data){
				$("#log_result").html(data);
			}
		});
	});

	$(function(){
		$("#comments").ajaxForm({
			beforeSend:function(){
				$("#com_result").html("<img src='images/loading.gif' width='30px' style='margin: 20px 0;'/>");
			},success:function(data){
				$("#com_result").html(data);
			}
		});
	});
	
	$(function(){
		$("#update-user").ajaxForm({
			beforeSend:function(){
				$("#up_result").html("<img src='images/loading.gif' width='30px' style='margin: 20px 0;'/>");
			},success:function(data){
				$("#up_result").html(data);
			}
		});
	});

});